DECLARE @eYear INT;
SET @eYear = CASE WHEN MONTH(GETDATE()) >= 8 THEN YEAR(DATEADD(YEAR, 1, GETDATE())) ELSE YEAR(GETDATE()) END;


SELECT p.personID,
	i.lastName + ', ' + i.firstName AS 'studentName'
FROM Enrollment AS e
	INNER JOIN [Identity] AS i ON i.personID = e.personID
	INNER JOIN Person AS p ON p.currentIdentityID = i.identityID AND p.personID = i.personID
	INNER JOIN Calendar AS cal ON cal.calendarID = e.calendarID AND cal.endYear = @eYear
	LEFT JOIN CustomStudent AS cs ON cs.enrollmentID = e.enrollmentID AND cs.attributeID = 903
WHERE e.endYear = @eYear
	AND e.active = 1 
	AND (e.endDate IS NULL OR GETDATE() <= e.endDate)
	AND ((e.serviceType = 'P' AND cs.[value] IS NULL) OR (e.serviceType = 'S' AND cs.[value] = '1'))
	AND cal.schoolID = 19
	AND e.personID IN
		(SELECT e.personID 
		FROM Enrollment AS e
			INNER JOIN Calendar AS cal ON cal.calendarID = e.calendarID
		WHERE e.active = 1
			AND e.startComments LIKE '%home%')

