DECLARE @eYear INT;
SET @eYear = CASE WHEN MONTH(GETDATE()) >= 8 THEN YEAR(DATEADD(YEAR, 1, GETDATE())) ELSE YEAR(GETDATE()) END;


--Transcript Duplicated non-repetable courses not marked as repeated
SELECT x.personID,
	x.[name],
	x.transcriptCourseName, 
	x.transcriptCourseNumber, 
	x.transcriptRepeatCourse, 
	COUNT(*) AS 'transcriptRepetCount'
FROM (	
	SELECT RANK() OVER (PARTITION BY p.personID ORDER BY e.grade DESC, e.startdate DESC) AS 'priority', 
		tc.personID,
		sch.[name],
		tc.courseName AS 'transcriptCourseName',
		tc.courseNumber AS 'transcriptCourseNumber',
		tc.repeatCourse AS 'transcriptRepeatCourse',
		sch.comments AS 'school'
	FROM Enrollment AS e
		INNER JOIN [Identity] AS i ON i.personID = e.personID
		INNER JOIN Person AS p ON p.currentIdentityID = i.identityID AND p.personID = i.personID
		INNER JOIN Calendar AS cal ON cal.calendarID = e.calendarID
		INNER JOIN School AS sch ON sch.schoolID = cal.schoolID
		INNER JOIN TranscriptCourse AS tc ON tc.personID = p.personID AND tc.grade = e.grade AND tc.schoolNumber = sch.[number] 
		INNER JOIN v_CourseInfo AS ci ON ci.courseNumber = tc.courseNumber AND ci.calendarID = e.calendarID AND ci.[repeatable] != 1
	WHERE cal.schoolID IN (18,19,20,22)
		AND e.serviceType = 'P'
		AND e.active = 1
		AND e.endYear >= @eYear - 4
		AND (tc.repeatCourse IS NULL OR tc.repeatCourse = 0)
		AND tc.courseNumber NOT IN ('56121', '56122', '56123', '57021', '57023', '32092', '32093', '51741', '51742', '51743', '51251', '51252', '51253', '55001', '55002', '55003', '52101', '52102', '52103', '57081', '56101', '56102', '56103', '53501', '53502', '53503', '57201', '57202', '57203', '53401', '53402', '53403', '51011', '51012', '51013', '')
		--AND e.personID = 5450
		--AND ci.courseNumber = '39401'
) AS x
WHERE x.[priority] =  1
GROUP BY x.personID,
	x.[name],
	x.transcriptCourseName,
	x.transcriptCourseNumber,
	x.transcriptRepeatCourse
HAVING COUNT(*) >= 2

DECLARE @eYear INT;
SET @eYear = CASE WHEN MONTH(GETDATE()) >= 8 THEN YEAR(DATEADD(YEAR, 1, GETDATE())) ELSE YEAR(GETDATE()) END;

--Active repeat courses not repeatble
SELECT DISTINCT p.personID,
	i.lastName + ', ' + i.firstName AS 'studentName',
	sch.comments AS 'activeSchool',
	ci.courseName AS 'currentCourseName',
	ci.courseNumber AS 'currentCourseNumber',
	te.[name] AS 'term',
	tc.courseName AS 'transcriptCourseName',
	tc.courseNumber AS 'transcriptCourseNumber',
	tc.grade AS 'transcriptGrade',
	tc.score AS 'transcriptScore'
FROM Enrollment AS e
	INNER JOIN [Identity] AS i ON i.personID = e.personID
	INNER JOIN Person AS p ON p.currentIdentityID = i.identityID AND p.personID = i.personID
	INNER JOIN TranscriptCourse AS tc ON tc.personID = p.personID
	--
	INNER JOIN Roster AS r ON r.personID = p.personID AND (r.endDate IS NULL OR GETDATE() <= r.endDate)
	INNER JOIN Section AS s ON s.sectionID = r.sectionID
	INNER JOIN v_CourseInfo AS ci ON ci.courseID = s.courseID AND (ci.[repeatable] != 1 OR ci.[repeatable] IS NULL)
	INNER JOIN Calendar AS cal ON cal.calendarID = ci.calendarID AND cal.calendarID = e.calendarID AND cal.endYear = @eYear
	INNER JOIN School AS sch ON cal.schoolID = sch.schoolID
	INNER JOIN ScheduleStructure AS ss ON ss.calendarID = cal.calendarID
	INNER JOIN Trial AS tl ON tl.structureID = ss.structureID AND tl.trialID = s.trialID AND tl.active = 1 
	INNER JOIN SectionPlacement AS sp ON sp.trialID = tl.trialID AND sp.sectionID = s.sectionID
	INNER JOIN Term AS te ON te.termID = sp.termID AND GETDATE() <= te.endDate
	LEFT JOIN CustomStudent AS cs ON cs.enrollmentID = e.enrollmentID AND cs.attributeID = 903
WHERE e.endYear = @eYear
	AND e.active = 1 
	AND (e.endDate IS NULL OR GETDATE() <= e.endDate)
	AND ((e.serviceType = 'P' AND cs.[value] IS NULL) OR (e.serviceType = 'S' AND cs.[value] = '1'))
	AND (ci.courseNumber = tc.courseNumber OR ci.courseName = tc.courseName)
	AND tc.grade IN ('09','10','11','12')
	AND cal.schoolID IN (18,19,20,22)




--Course Mismtach for transfer students
SELECT p.personID,
	i.lastName + ', ' + i.firstName AS 'studentName',
	tc.courseName AS 'transcriptCourseName',
	tc.courseNumber AS 'transcriptCourseNumber',
	tc.comments,
	CONVERT(DATE, tc.modifiedDate, 110) AS 'modifiedOn',
	mp.personID,
	mi.lastName + ', ' + mi.firstName AS 'modifiedBy'
FROM TranscriptCourse AS tc 
	LEFT JOIN Person AS mp ON mp.personID = tc.modifiedByID
	LEFT JOIN [Identity] AS mi ON mi.identityID = mp.currentIdentityID AND mi.personID = mp.personID
	INNER JOIN Person AS p ON p.personID = tc.personID
	INNER JOIN [Identity] AS i ON i.identityID = p.currentIdentityID AND i.personID = p.personID
	INNER JOIN Enrollment AS e ON e.personID = i.personID
	INNER JOIN Calendar AS cal ON e.calendarID = cal.calendarID AND cal.schoolID IN (18,19,20,22)
	LEFT JOIN CustomStudent AS cs ON cs.enrollmentID = e.enrollmentID AND cs.attributeID = 903
WHERE e.startStatus IN ('2A', '2B', '2C')
	AND tc.comments IS NOT NULL
	AND tc.courseNumber NOT LIKE '9%%%%'
