--Credit Mistmatch
SELECT DISTINCT tcred.creditID,
	tc.courseName, 
	p.personID,
	i.lastName + ', ' + i.firstName AS 'studentName',
	tcred.creditsEarned,
	tcred.creditsAttempted,
	CONVERT(DATE, tc.modifiedDate, 110) AS 'modifiedOn',
	mp.personID,
	mi.lastName + ', ' + mi.firstName AS 'modifiedBy'
FROM TranscriptCredit AS tcred
	INNER JOIN TranscriptCourse AS tc ON tc.transcriptID = tcred.transcriptID
	LEFT JOIN Person AS mp ON mp.personID = tc.modifiedByID
	LEFT JOIN [Identity] AS mi ON mi.identityID = mp.currentIdentityID AND mi.personID = mp.personID
	INNER JOIN Person AS p ON p.personID = tc.personID
	INNER JOIN [Identity] AS i ON i.identityID = p.currentIdentityID AND i.personID = p.personID
	INNER JOIN Enrollment AS e ON e.personID = i.personID
	INNER JOIN Calendar AS cal ON e.calendarID = cal.calendarID --AND cal.schoolID = 19
WHERE tcred.creditsAttempted = 1 AND tcred.creditsEarned NOT IN (0.000,1.000)
ORDER BY p.personID DESC


--Check GPA of studnets not within highlands boundaries
DECLARE @eYear INT;
SET @eYear = CASE WHEN MONTH(GETDATE()) >= 8 THEN YEAR(DATEADD(YEAR, 1, GETDATE())) ELSE YEAR(GETDATE()) END;

SELECT p.personID,
	e.grade,
	i.lastName + ', ' + i.firstName AS 'studentName',
	sch.comments AS 'attendingSchool',
	CASE sb.schoolID
		WHEN 18 THEN 'PHS'
		WHEN 20 THEN 'CHS'
		END AS 'schoolOfRecord',
	cs.[value] AS 'unweightedGPA' 
FROM Enrollment AS e
	INNER JOIN [Identity] AS i ON i.personID = e.personID
	INNER JOIN Person AS p ON p.currentIdentityID = i.identityID AND p.personID = i.personID
	INNER JOIN Calendar AS cal ON cal.calendarID = e.calendarID AND cal.schoolID = 19
	INNER JOIN School AS sch ON sch.schoolID = cal.schoolID
	INNER JOIN HouseholdMember hm ON hm.personID = p.personID AND hm.[secondary] = 0 AND (hm.endDate IS NULL OR GETDATE() <= hm.endDate)
	INNER JOIN Household h ON h.householdID = hm.householdID
	INNER JOIN HouseholdLocation hl ON hl.householdID = h.householdID AND  hl.[secondary] = 0 AND (hl.endDate IS NULL OR GETDATE() <= hl.endDate)
	INNER JOIN [Address] a ON a.addressID = hl.addressID
	INNER JOIN SchoolBoundary AS sb ON sb.addressID = a.addressID AND sb.schoolID IN (18,20)
	LEFT JOIN ProgramParticipation AS pp ON pp.personID = p.personID AND pp.programID = 14
	INNER JOIN CustomStudent AS cs ON cs.personID = p.personID AND cs.attributeID  = 915
WHERE e.endYear = @eYear	
		AND e.active = 1 
		AND (e.endDate IS NULL OR GETDATE() <= e.endDate)
		AND e.grade != 'NG' 
		AND e.serviceType = 'P'
		AND (pp.programID != 14 OR pp.programID IS NULL)
ORDER BY unweightedGPA ASC


